BEGIN TRANSACTION;
CREATE TABLE activity_comments (
	id INTEGER NOT NULL, 
	comment_text VARCHAR(500) NOT NULL, 
	recommendation_id INTEGER NOT NULL, 
	family_member_id INTEGER NOT NULL, 
	created_at DATETIME, 
	updated_at DATETIME, 
	PRIMARY KEY (id), 
	FOREIGN KEY(recommendation_id) REFERENCES activity_recommendations (id), 
	FOREIGN KEY(family_member_id) REFERENCES family_members (id)
);
CREATE TABLE activity_favorites (
	id INTEGER NOT NULL, 
	favorite_date DATETIME NOT NULL, 
	notes VARCHAR(200), 
	recommendation_id INTEGER NOT NULL, 
	family_member_id INTEGER NOT NULL, 
	created_at DATETIME, 
	updated_at DATETIME, 
	PRIMARY KEY (id), 
	CONSTRAINT unique_favorite_per_member UNIQUE (recommendation_id, family_member_id), 
	FOREIGN KEY(recommendation_id) REFERENCES activity_recommendations (id), 
	FOREIGN KEY(family_member_id) REFERENCES family_members (id)
);
